import { useState } from "react";
import Chevron from '../imageHome/Chevron.png'
import divider from '../imageHome/Divider.png'

const FAQ = ({ tittle, text, id }) => {
    const [toggle, setToggle] = useState(false);



    return (
        <div className="bg-white flex flex-col w-668px p-22px rounded-10px cursor-pointer transition-all duration-200 ease-in-out" key={id} onClick={() => setToggle(!toggle)}>
    <div className="flex flex-row justify-between text-18px font-semibold">
        {tittle} 
        <img src={Chevron} alt='Chevron'
            className={`rotate-0 transition duration-200 ease-in-out transform ${toggle ? "rotate-180" : "rotate-0" }`}
        />
    </div>
    <div className={`translate-y-1 text-12px text-start ${toggle ? "transition-all duration-200 ease-in-out opacity-100 scale-100" : "transition-all duration-200 ease-in-out opacity-0 scale-0"}`}>
        <img className="py-4" src={divider} alt="FAQ" />
        {text}
    </div>
</div>


    );
};
export default FAQ
